<?php namespace Application\Controllers; use GL\Core\Controller as Controller;use Symfony\Component\HttpFoundation\Request as Request;  class ErrorController extends Controller{    public function error500($message,$file,$line,$errors)    {            $this->render('500.html.twig',array('message'=>$message,'file'=>$file,'line'=>$line,'errors'=>$errors),500);		    }	        public function error404()    {        $this->render('404.html.twig',array(),404);	    }        public function error403()    {        $this->render('403.html.twig',array(),403);    }}